ACTION.CODE:
String sel = view.getTextArea().getSelectedText();
String text = (sel != null && sel.trim().length() > 0) ? sel :
	isabelle.assistant.CommandExtractor.getCommandAtOffset(view.getBuffer(), view.getTextArea().getCaretPosition()).getOrElse("");
if (text.length() > 0) isabelle.assistant.ExplainAction.explain(view, text);

org.gjt.sp.jedit.gui.DockableWindowManager dwm = view.getDockableWindowManager();
if (dwm != null) {
	dwm.showDockableWindow("assistant-dockable");
	Object dockable = dwm.getDockable("assistant-dockable");
	if (dockable != null && dockable instanceof isabelle.assistant.AssistantDockable) {
		((isabelle.assistant.AssistantDockable) dockable).focusOnDefaultComponent();
	}
}
