ACTION.CODE:
String sel = view.getTextArea().getSelectedText();
String text = (sel != null && sel.trim().length() > 0) ? sel :
	isabelle.copilot.CommandExtractor.getCommandAtOffset(view.getBuffer(), view.getTextArea().getCaretPosition()).getOrElse("");
if (text.length() > 0) isabelle.copilot.ExplainAction.explain(view, text);

org.gjt.sp.jedit.gui.DockableWindowManager dwm = view.getDockableWindowManager();
if (dwm != null) {
	dwm.showDockableWindow("copilot-dockable");
	Object dockable = dwm.getDockable("copilot-dockable");
	if (dockable != null && dockable instanceof isabelle.copilot.CopilotDockable) {
		((isabelle.copilot.CopilotDockable) dockable).focusOnDefaultComponent();
	}
}
